import { useEffect, useContext } from 'react';
import './App.css';
import LoginForm from './components/LoginForm';
import { Context } from './index';

function App() {

  const {store} = useContext(Context)

  useEffect(() => {
    if (localStorage.getItem('token')) {
      store.checkAuth()      
    }
  }, [])


  return (
    <div className="App">
      <h1>{store.isAuth}</h1>
      <LoginForm/>
    </div>
  );
}

export default App;
